import React, { useEffect, useRef } from "react";

const UseRefComp = () => {
  const amountRef = useRef();

  useEffect(() => console.log(amountRef.current.value), [
    amountRef.current.value,
  ]);

  return (
    <div>
      <input ref={amountRef} />
    </div>
  );
};

export default UseRefComp;
