import React, { useState } from "react";

import "./App.css";

function App() {
  const [inputText, setInput] = useState("");
  const [count, setCount] = useState(0);

  return (
    <div className="App">
      <input type="text" onChange={(e) => setInput(e.target.value)} />
      <div>{inputText}</div>

      <button onClick={() => setCount((c) => c + 1)}>counter up</button>
      <div>{count}</div>
    </div>
  );
}

export default App;
