import React, { useEffect, useRef } from "react";

const UseRefComp = () => {
  const amountRef = useRef();

  useEffect(() => amountRef.current.focus());

  return (
    <div>
      <input ref={amountRef} type="text" />
      <button>ads</button>
    </div>
  );
};

export default UseRefComp;
