import React, { useState, useEffect, useRef } from "react";

import "./App.css";

function App() {
  // const [inputText, setInput] = useState("");
  const [count, setCount] = useState(0);
  const [title, setTitle] = useState("");

  const amountRef = useRef();

  const handleCounter = () => setCount((c) => c + 1);
  const handleTitle = () => setTitle("Zayan Page");

  useEffect(() => {
    console.log("effet 1");
    document.title = title;
  }, [title]);

  useEffect(() => {
    console.log("effet 2");
    document.title = ` u clicked ${count} `;
  }, [count]);

  return (
    <div className="App">
      {/* useState */}
      {/* <input type="text" onChange={(e) => setInput(e.target.value)} />
      <div>{inputText}</div> */}

      <button onClick={handleCounter}>counter up</button>
      <div>{count}</div>
      <button onClick={handleTitle}>change title</button>
    </div>
  );
}

export default App;
