import React, { useState } from "react";

import "./App.css";

function App() {
  const [inputText, setInput] = useState("");

  return (
    <div className="App">
      <input type="text" onChange={(e) => setInput(e.target.value)} />
      <div>{inputText}</div>
    </div>
  );
}

export default App;
